sources.sass\\_global.scss:
@import './variables';

html {
  font-size: 100%;
  box-sizing: border-box;
}

*,
*::after,
*::before {
  box-sizing: inherit;
  margin: 0;
  padding: 0;
}

a {
  text-decoration: none;
  padding: 0;
  margin: 0;
  user-select: none;
}

img {
  width: 100%;
  user-select: none;
}

body {
  font-family: $barlow;
  font-size: $body;
  font-weight: 400;
  line-height: 26px;
  background-color: $lightCream;
  padding: 0 5em;
  scroll-behavior: smooth;

  @media screen and(max-width: 1270px) and (min-width: 1024px) {
    padding: 0 2.5em;
  }

  @media screen and(max-width: 1023px) and (min-width: 768px) {
    padding: 0 2.5em;
  }

  @media screen and(max-width: 700px) {
    padding: 0 1.5em; //24px
  }
}

h1 {
  font-family: $fraunces;
  font-weight: 900;
  font-size: 4.5rem;
  line-height: 72px;
  margin: 0;
  padding: 0;
}

h2 {
  font-family: $fraunces;
  font-weight: 900;
  font-size: 2.5rem;
  line-height: 48px;
  margin: 0;
  padding: 0;
}

h3 {
  font-family: $fraunces;
  font-weight: 900;
  font-size: 2rem;
  line-height: 36px;
  margin: 0;
  padding: 0;
}

h4 {
  font-family: $fraunces;
  font-weight: 900;
  font-size: 1.5rem;
  line-height: 32px;
  margin: 0;
  padding: 0;
}

p {
  margin: 0;
  padding: 0;
}

.btn__primary {
  background-color: $darkCyan;
  font-family: 'Fraunces', serif;
  font-size: 1.125rem;
  color: $lightCream;
  padding: 0.9375em 1.9375em 1em 1.9375em;
  border-radius: 6px;
  user-select: none;
  transition: all 200ms ease-in-out;

  &:hover {
    background-color: $btnHover;
  }
}

.logo {
  display: flex;
  align-items: center;
  user-select: none;

  img {
    @media screen and(max-width: 390px) {
      width: 16px;
    }
  }

  .logo__text {
    font-size: 1.5625rem;
    font-family: $fraunces;
    margin-left: 0.2em;

    @media screen and(max-width: 390px) {
      font-size: 1.2rem;
    }
  }

  .lf {
    color: $lightCream;
  }
}

.main {
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
}

.overlay__menu {
  @media screen and(max-width: 700px) {
    position: absolute;
    top: 113px;
    left: 0;
    width: 100%;
    height: 0;
    background: linear-gradient(
      180deg,
      rgba(254, 252, 247, 1) 36%,
      rgba(254, 252, 247, 0.2)
    );
    visibility: hidden;
    z-index: 1;
    transition: all 500ms ease-in-out;
  }

  @media screen and(max-width: 390px) {
    top: 90px;
  }
}

