@import './variables';
@import './global';

.header {
  width: 100%;
  height: 113px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  z-index: 10;

  @media screen and(max-width: 390px) {
    height: 90px;
  }

  .mobile__menu {
    display: none;

    @media screen and(max-width: 700px) {
      display: block;
      font-size: 1.25rem; //20px
      cursor: pointer;
    }
  }

  .mobile__close {
    font-size: 1.5rem; //20px
    cursor: pointer;
    display: none;
  }

  @media screen and(max-width: 1270px) and (min-width: 1024px) {
    padding: 2.5em;
  }

  @media screen and(max-width: 1023px) {
    padding: 0;
  }

  .nav__top {
    display: flex;

    @media screen and(max-width: 700px) {
      position: absolute;
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: center;
      background-color: $lightCream;
      width: 100%;
      height: 0;
      top: 113px;
      left: 0;
      z-index: 2;
      transition: height 400ms ease-in-out;
    }

    @media screen and(max-width: 390px) {
      top: 90px;
    }

    a {
      font-family: $barlow;
      font-weight: 700;
      font-size: 0.75rem;
      line-height: 1;
      color: $gray;
      text-transform: uppercase;
      transition: color 200ms ease-in-out;

      &:not(:last-child) {
        margin-right: 33px;
      }

      &:hover {
        color: $darkGray;
      }

      @media screen and(max-width: 700px) {
        font-size: 0;
        font-family: $fraunces;
        line-height: 32px;
        text-transform: capitalize;
        color: $darkGray;
        visibility: hidden;
        transition-property: visibility, font-size;
        transition: 300ms ease-in-out;

        &:not(:last-child) {
          margin-right: 0;
          margin-bottom: 32px;
        }
      }
    }
  }
}

sources.sass\\_hero.scss:
@import './variables';

.hero__sec {
  width: 100%;
  height: auto;
  position: relative;
  margin-bottom: 12.5em;

  @media screen and(max-width: 1270px) and (min-width: 1024px) {
    margin-bottom: 8em;
  }

  @media screen and(max-width: 1023px) {
    margin-bottom: 9em; //144px
  }

  @media screen and(max-width: 700px) {
    margin-bottom: 5em; //80px
  }

  .hero__bg {
    width: 100%;
    background-image: url(../assets/home/desktop/image-hero-coffeepress.jpg);
    background-size: cover;
    background-repeat: no-repeat;
    background-position: center top;
    padding: 7.5em 5.3em; //120px 85px
    border-radius: 8px;

    @media screen and(max-width: 1270px) and (min-width: 1024px) {
      padding: 7.5em 3em;
    }

    @media screen and(max-width: 1023px) {
      background-image: url(../assets/home/tablet/image-hero-coffeepress.jpg);

      padding: 6.6em 3.6em;
    }

    @media screen and(max-width: 700px) {
      background-image: url('../assets/home/mobile/image-hero-coffeepress.jpg');
      background-position: bottom center;
      padding: 6.25em 1.5em; // 100px 24px
    }

    @media screen and(max-width: 470px) {
      background-position: top center;
    }

    .hero__copy {
      display: flex;
      flex-direction: column;
      justify-content: center;
      max-width: 493px;

      @media screen and(max-width: 1023px) {
        max-width: 410px;
      }

      @media screen and(max-width: 700px) {
        max-width: none;
        align-items: center;
        padding: 0 5em;
      }

      @media screen and(max-width: 540px) {
        padding: 0;
      }

      h1 {
        font-size: 4.5rem;
        color: $lightCream;
        margin-bottom: 0.5em;

        @media screen and(max-width: 1270px) and (min-width: 1024px) {
          font-size: 3.8rem;
          line-height: 1.2em;
          margin-bottom: 0.4em;
        }

        @media screen and(max-width: 1023px) {
          font-size: 3rem;
          line-height: 48px;
        }

        @media screen and(max-width: 700px) {
          text-align: center;
          font-size: 2.5rem; //40px
          line-height: 40px;
        }

        @media screen and(max-width: 370px) {
          font-size: 1.9rem; //30px
        }
      }

      p {
        line-height: 1.625rem;
        color: $lightCream;
        margin-bottom: 3.5em;
        padding-right: 3.5em;
        opacity: 0.8;

        @media screen and(max-width: 1023px) {
          padding-right: 1em;
        }

        @media screen and(max-width: 700px) {
          padding-right: 0;
          text-align: center;
        }
      }

      .hero__button {
        width: 100%;
        margin-top: 1em;
        margin-bottom: 1em;

        @media screen and(max-width: 700px) {
          display: flex;
          justify-content: center;
        }
      }
    }
  }
}

sources.sass\\_collection.scss:
@import './global';
@import './variables';

.collection__sec {
  width: 100%;
  display: flex;
  flex-direction: column;
  padding: 0 5.3em; //85px
  margin-bottom: 12.5em; //200px

  @media screen and(max-width: 1440px) and (min-width: 1024px) {
    padding: 0 2em;
  }

  @media screen and(max-width: 1023px) {
    padding: 0;
    margin-bottom: 9em; //144px
  }

  @media screen and(max-width: 700px) {
    margin-bottom: 4.4em; //70px
  }

  .collection__title {
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 2.5em 0;
    position: relative;
    margin-bottom: -2.8em;

    @media screen and(max-width: 1023px) {
      margin-bottom: -3.1em;
    }

    @media screen and(max-width: 700px) {
      margin-bottom: 0.7em; //11px
    }

    &::after {
      content: '';
      position: absolute;
      top: 0em;
      left: 0;
      width: 100%;
      height: 100%;
      background: linear-gradient(
        0deg,
        rgba(254, 252, 247, 1),
        rgba(254, 252, 247, 0.014740930551908282)
      );
    }

    h2 {
      font-size: 9.9rem; //158px
      opacity: 0.2;
      text-transform: lowercase;

      @media screen and(max-width: 1350px) {
        font-size: 9rem;
      }

      @media screen and(max-width: 1270px) {
        font-size: 7rem;
      }

      @media screen and(max-width: 1023px) {
        font-size: 6rem;
      }

      @media screen and(max-width: 767px) {
        font-size: 3.9rem;
        opacity: 0.4;
      }

      @media screen and(max-width: 500px) {
        font-size: 3rem;
      }

      @media screen and(max-width: 390px) {
        font-size: 2.5rem;
      }

      @media screen and(max-width: 370px) {
        font-size: 2.2rem;
      }
    }
  }

  .collection__cards {
    width: 100%;
    display: flex;
    justify-content: center;
    padding-left: 1em;
    z-index: 1;

    @media screen and(max-width: 1023px) {
      flex-direction: column;
    }

    @media screen and(max-width: 1023px) {
      padding: 0 3.6em; //57px
    }

    @media screen and(max-width: 540px) {
      padding: 0 1em;
    }

    .collection__card {
      width: 100%;

      @media screen and(max-width: 1023px) {
        display: flex;
        margin-bottom: 2em; //32px
      }

      @media screen and(max-width: 700px) {
        flex-direction: column;
        align-items: center;
        margin-bottom: 4.4em; //70px
      }

      .card__image {
        width: 100%;
        display: flex;
        justify-content: center;

        img {
          max-width: 255px;

          @media screen and(max-width: 1270px) and (min-width: 1024px) {
            max-width: 200px;
          }
        }
      }

      .card__copy {
        width: 100%;
        text-align: center;
        margin-top: 4.4375em; //px

        @media screen and(max-width: 1023px) {
          text-align: start;
          margin: 0.8em 0 0 0;
        }

        @media screen and(max-width: 700px) {
          text-align: center;
        }

        h3 {
          font-size: 1.5rem;
          margin-bottom: 1em;
        }

        p {
          padding: 0 1.5em;

          @media screen and(max-width: 1023px) {
            padding: 0;
          }
        }
      }
    }
  }
}

sources.sass\\_why.scss:
@import './variables';
@import './global';

.why__sec {
  max-width: 1280px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background-color: $darkGray;
  padding: 6.25em 5.3em 0em 5.3em;
  border-radius: 8px;
  margin-bottom: 22em; //351px

  @media screen and(max-width: 1270px) and (min-width: 1024px) {
    padding: 6.25em 2.5em 0em 2.5em;
  }

  @media screen and(max-width: 1023px) {
    padding: 3.5em 3.6em 0 3.6em; //56px 57px 0 57px
  }

  @media screen and(max-width: 700px) {
    margin-bottom: 20em; //320px
  }

  @media screen and(max-width: 590px) {
    padding: 4.6em 1.5em 0 1.5em; //74px 24px 0 24px
    margin-bottom: 36.9em; //590px
  }

  .why__copy {
    text-align: center;
    max-width: 540px;

    @media screen and(max-width: 1023px) {
      padding: 0 1.2em;
    }

    @media screen and(max-width: 590px) {
      padding: 0;
    }

    h2 {
      color: $lightCream;
      margin-bottom: 0.8em;

      @media screen and(max-width: 590px) {
        font-size: 1.75rem; //28px
      }

      @media screen and(max-width: 370px) {
        font-size: 1.6rem; //25px
      }
    }

    p {
      color: $lightCream;
      margin-bottom: 5.3125em;
      opacity: 0.8;

      @media screen and(max-width: 1023px) {
        margin-bottom: 4.4em; //70px
      }
    }
  }

  .why__cards {
    width: 100%;
    display: flex;
    margin-bottom: -9.375em;

    @media screen and(max-width: 1023px) {
      flex-direction: column;
      margin-bottom: -20em; //-320px
    }

    @media screen and(max-width: 768px) {
      margin-bottom: -22.5em; //-360px
    }

    @media screen and(max-width: 700px) {
      margin-bottom: -21.9em; //-350px
    }

    @media screen and(max-width: 590px) {
      margin-bottom: -37.5em; //-600px
    }

    .why__card {
      width: 100%;
      display: flex;
      flex-direction: column;
      background-color: $darkCyan;
      padding: 4.5em 3em 3em 3em;
      border-radius: 8px;

      @media screen and(max-width: 1270px) and (min-width: 1024px) {
        padding: 4em 2em 3em 2em;
      }

      @media screen and(max-width: 1023px) {
        flex-direction: row;
        align-items: center;
        padding: 2.6em 3em 2.6em 4.4em; //41px 48px 41px 70px
        margin: 0 0 1.5em 0; //0 0 24px 0
      }

      @media screen and(max-width: 590px) {
        flex-direction: column;
        padding: 4.5em 2em 3.2em 2em; //72px 33px 51px 33px
      }

      &:not(:last-child) {
        margin-right: 1em;
      }

      .card__image {
        min-height: 72px;
        margin-bottom: 3.5em;
        text-align: center;

        @media screen and(max-width: 1023px) {
          margin: 0;
        }

        @media screen and(max-width: 590px) {
          margin-bottom: 3.5em; //56px
        }

        img {
          width: 72px;
        }
      }

      .card__copy {
        width: 100%;
        text-align: center;

        @media screen and(max-width: 1023px) {
          text-align: start;
          margin-left: 3.43em; //55px
        }

        @media screen and(max-width: 590px) {
          text-align: center;
          margin: 0;
        }

        h3 {
          font-size: 24px;
          line-height: 32px;
          color: $lightCream;
          margin-bottom: 1em;
        }

        p {
          color: $lightCream;
        }
      }
    }
  }
}

sources.sass\\_how.scss:
@import './global';
@import './variables';

.how__sec {
  width: auto;
  margin-left: 5em; //80px
  margin-bottom: 12.5em; //200px

  @media screen and(max-width: 1270px) and (min-width: 1024px) {
    margin-left: 0px;
    padding-left: 2.5em;
  }

  @media screen and(max-width: 1023px) {
    margin: 6.25em 0 8.75em 0; //100px 0 140px 0
  }

  .how__title {
    width: 100%;

    @media screen and(max-width: 767px) {
      text-align: center;
      display: flex;
      flex-direction: column;
      justify-content: center;
    }

    h2 {
      font-size: 24px;
      color: $gray;
      margin-bottom: 3.4em; //80px
    }
  }

  .how__cards {
    width: 100%;
    display: flex;
    margin-bottom: 4em;

    @media screen and(max-width: 767px) {
      flex-direction: column;
    }

    .how__card {
      max-width: 285px;
      min-width: 200px;
      position: relative;

      &:not(:last-child) {
        margin-right: 5.9375em;
      }

      @media screen and(max-width: 1023px) {
        max-width: 223px;

        &:not(:last-child) {
          margin-right: 2em;
        }
      }

      @media screen and(max-width: 767px) {
        text-align: center;
        max-width: none;
        min-width: none;

        &:not(:last-child) {
          margin: 0 0 3.5em 0; // 0 0 56px 0
        }
      }

      .progress__bar {
        width: 100%;
        display: flex;
        align-items: center;
        margin-bottom: 4.2em; //67px

        @media screen and(max-width: 767px) {
          display: none;
        }

        span {
          font-size: 31px;
          color: $darkCyan;
          background-color: $lightCream;

          &:not(:last-child)::before {
            content: '';
            position: absolute;
            background-color: $paleOrange;
            width: 349px;
            height: 2px;
            left: 31px;
            top: 13.5px;

            @media screen and(max-width: 1270px) {
              width: 350px;
            }

            @media screen and(max-width: 1085px) {
              width: 319px;
            }

            @media screen and(max-width: 1023px) {
              width: 230px;
            }
          }
        }
      }

      .numbers {
        width: 100%;
        margin-bottom: 2.375em;

        .number {
          font-family: $fraunces;
          font-size: 4.5rem;
          line-height: 4.5rem;
          color: $paleOrange;
        }
      }

      .card__copy {
        width: 100%;

        .card__title {
          margin-bottom: 1.3125em;
          padding-right: 1em;

          @media screen and(max-width: 700px) {
            margin-bottom: 0.9em; //24px
            font-size: 1.75rem; //28px
            line-height: 32px;
          }

          @media screen and(max-width: 780px) {
            padding-right: 0;
          }
        }

        p {
          @media screen and(max-width: 1023px) {
            padding-right: 0;
          }
        }
      }
    }
  }

  .how__btn {
    @media screen and(max-width: 767px) {
      display: flex;
      justify-content: center;
    }
  }
}

sources.sass\\_footer.scss:
@import './variables';
@import './global';

.footer {
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: space-between;
  background-color: $darkGray;
  padding: 2.9375em 5.3125em; //47px 85px
  margin-bottom: 5.5em; //88px

  @media screen and(max-width: 1023px) {
    flex-direction: column;
    padding: 3.4em 11.9em 3.4em 13.5em; //54px 190px 54px 215px
  }

  @media screen and(max-width: 1270px) and (min-width: 1024px) {
    padding: 2.9375em 2.5em;
  }

  @media screen and(max-width: 1023px) {
    padding: 2.5em 2em;
  }

  .logo__nav {
    display: flex;
    align-items: center;

    @media screen and(max-width: 1023px) {
      flex-direction: column;
    }

    .logo {
      margin-right: 4.08em;

      @media screen and(max-width: 1023px) {
        margin: 0 0 2em 0;
      }
    }

    .nav__footer {
      display: flex;

      @media screen and(max-width: 390px) {
        flex-direction: column;
        align-items: center;
      }

      a {
        font-family: $barlow;
        font-weight: 700;
        font-size: 0.75rem;
        line-height: 1;
        color: $gray;
        text-transform: uppercase;
        transition: color 200ms ease-in-out;

        &:not(:last-child) {
          margin-right: 2.75em; //33px
        }

        &:hover {
          color: $lightCream;
        }

        @media screen and(max-width: 390px) {
          &:not(:last-child) {
            margin-right: 0;
            margin-bottom: 2em; //24px
          }
        }
      }
    }
  }

  .social_icons {
    @media screen and(max-width: 1023px) {
      margin-top: 4.1em; //65px
    }

    .icon {
      font-size: 1.5rem; //24px
      color: $lightCream;
      transition: color 200ms ease-in-out;

      &:not(:last-child) {
        margin-right: 1.5em;
      }

      &:hover {
        color: $paleOrange;
      }
    }
  }
}
